function bringmenu(){document.documentElement.scrollTop>450?(document.getElementById("hiddenmenu").classList.add("show"),document.querySelector("nav").classList.add("header__nav_nomarg"),document.getElementById("header__space").style.display="block"):(document.getElementById("hiddenmenu").classList.remove("show"),document.querySelector("nav").classList.remove("header__nav_nomarg"),document.getElementById("header__space").style.display="none")}window.addEventListener("scroll",bringmenu);